import React, { useState, useEffect } from 'react';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './App.css';

function App() {
    const [middleSize, setMiddleSize] = useState(2);
    const [data, setData] = useState([]);
    const [stats, setStats] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchData();
    }, [middleSize]);

    const fetchData = async () => {
        try {
            setLoading(true);

            // Fetch middle analysis data
            const response = await fetch(`http://localhost:5000/api/middle-analysis?middle_size=${middleSize}`);
            if (!response.ok) throw new Error('Failed to fetch data');
            const result = await response.json();

            if (result.success) {
                setData(result.data);
            } else {
                throw new Error(result.error || 'Unknown error');
            }

            // Fetch database stats
            const statsResponse = await fetch('http://localhost:5000/api/database-stats');
            if (statsResponse.ok) {
                const statsResult = await statsResponse.json();
                if (statsResult.success) {
                    setStats(statsResult.stats);
                }
            }

            setError(null);
        } catch (err) {
            setError(err.message);
            console.error('Error fetching data:', err);
        } finally {
            setLoading(false);
        }
    };

    const formatPercentage = (value) => `${(value * 100).toFixed(1)}%`;

    if (loading) {
        return (
            <div style={{ padding: '20px', textAlign: 'center' }}>
                <h2>Loading data...</h2>
                <p>Make sure the backend is running on http://localhost:5000</p>
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ padding: '20px', color: 'red' }}>
                <h2>Error: {error}</h2>
                <p>Make sure the backend server is running:</p>
                <code>python middle_analysis_backend.py</code>
            </div>
        );
    }

    return (
        <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
            <h1>eBasketball Spread Middle Analysis Dashboard</h1>

            {/* Database Stats */}
            <div style={{
                background: '#f0f0f0',
                padding: '15px',
                borderRadius: '8px',
                marginBottom: '20px',
                display: 'flex',
                justifyContent: 'space-around'
            }}>
                <div>
                    <strong>Total Games:</strong> {stats.total_games || 0}
                </div>
                <div>
                    <strong>Opener Spreads:</strong> {stats.opener_spreads || 0}
                </div>
                <div>
                    <strong>Quarter Lines:</strong> {stats.quarter_lines || 0}
                </div>
            </div>

            {/* Middle Size Selector */}
            <div style={{ marginBottom: '20px' }}>
                <label>Middle Size: </label>
                <select
                    value={middleSize}
                    onChange={(e) => setMiddleSize(parseInt(e.target.value))}
                    style={{ marginLeft: '10px', padding: '5px' }}
                >
                    {[1, 2, 3, 4, 5].map(size => (
                        <option key={size} value={size}>{size} points</option>
                    ))}
                </select>
            </div>

            {/* Hit Rate Comparison Chart */}
            <div style={{ marginBottom: '40px' }}>
                <h2>Hit Rates by Capture Point ({middleSize}-point middle)</h2>
                <ResponsiveContainer width="100%" height={400}>
                    <BarChart data={data}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="capture" />
                        <YAxis tickFormatter={formatPercentage} />
                        <Tooltip formatter={formatPercentage} />
                        <Legend />
                        <Bar
                            dataKey="lowHitRate"
                            fill="#8884d8"
                            name="Low Side Only"
                        />
                        <Bar
                            dataKey="highHitRate"
                            fill="#82ca9d"
                            name="High Side Only"
                        />
                        <Bar
                            dataKey="eitherHitRate"
                            fill="#ffc658"
                            name="Either Side"
                        />
                        {/* Break-even line at 9.09% */}
                        <Line
                            type="monotone"
                            dataKey={() => 0.0909}
                            stroke="#ff0000"
                            strokeDasharray="5 5"
                            name="Break-even (9.09%)"
                        />
                    </BarChart>
                </ResponsiveContainer>
            </div>

            {/* Detailed Stats Table */}
            <div>
                <h2>Detailed Statistics</h2>
                <table style={{
                    width: '100%',
                    borderCollapse: 'collapse',
                    border: '1px solid #ddd'
                }}>
                    <thead>
                        <tr style={{ background: '#f0f0f0' }}>
                            <th style={{ padding: '10px', textAlign: 'left', border: '1px solid #ddd' }}>
                                Capture Point
                            </th>
                            <th style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                Games
                            </th>
                            <th style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                Low Hits
                            </th>
                            <th style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                High Hits
                            </th>
                            <th style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                Either Hits
                            </th>
                            <th style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                Either Hit Rate
                            </th>
                            <th style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                Profitable?
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((row, idx) => {
                            const isProfitable = row.eitherHitRate >= 0.0909;
                            return (
                                <tr key={idx}>
                                    <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                                        {row.capture}
                                    </td>
                                    <td style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                        {row.totalGames}
                                    </td>
                                    <td style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                        {row.lowHits}
                                    </td>
                                    <td style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                        {row.highHits}
                                    </td>
                                    <td style={{ padding: '10px', textAlign: 'center', border: '1px solid #ddd' }}>
                                        {row.eitherHits}
                                    </td>
                                    <td style={{
                                        padding: '10px',
                                        textAlign: 'center',
                                        border: '1px solid #ddd',
                                        fontWeight: 'bold',
                                        color: isProfitable ? 'green' : 'black'
                                    }}>
                                        {formatPercentage(row.eitherHitRate)}
                                    </td>
                                    <td style={{
                                        padding: '10px',
                                        textAlign: 'center',
                                        border: '1px solid #ddd',
                                        background: isProfitable ? '#d4edda' : '#f8d7da',
                                        color: isProfitable ? '#155724' : '#721c24'
                                    }}>
                                        {isProfitable ? 'YES' : 'NO'}
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>

            {/* Key Insights */}
            <div style={{
                marginTop: '30px',
                padding: '20px',
                background: '#e8f4f8',
                borderRadius: '8px'
            }}>
                <h3>Key Insights</h3>
                <ul>
                    <li>Break-even hit rate: 9.09% (for -120 odds on both sides)</li>
                    <li>Hit rates above 9.09% are theoretically profitable</li>
                    <li>"Either Side" means the middle hits if EITHER the low OR high window wins</li>
                    <li>Q3 End showing exceptionally high hit rates suggests significant line movement in the final quarter</li>
                </ul>
            </div>
        </div>
    );
}

export default App;
